import React, { Component } from 'react';
import axios from "axios";

export class HTTPRequests extends Component {
  constructor(props) {
    super(props)

    this.state = {
       posts: [],
       error: null
    }
  }

  componentDidMount() {
    axios.get("https://jsonplaceholder.typicode.com/posts/100")
    .then(response => {
      console.log(response.data)
      this.setState({
        posts: Array.isArray(response.data) ? response.data : [response.data]
      })
    })
    .catch(error => {
      this.setState({
        error: error.message
      })
    })
  }

  render() {
    const posts = this.state.posts;
    return (
      <div>
        <h2>Posts:</h2>
        {
          posts.length ? (
            posts.map(post => (
              <div key={post.id}>
                <h2>{post.id}. {post.title}</h2>
                <h4>By User ID {post.userId}</h4>
                <p>{post.body}</p>
                <hr />
              </div>
            ))
          ) : (
          <h4>Loading posts...</h4>
          )
        }
      </div>
    )
  }
}

export default HTTPRequests


// npm install axios
// import axios into the component
// call axios.get("API_URL") in componentDidMount() to fetch data
// call setState to update the data in the state
// render the UI based on the new state values
